import section from "../section/uiSchema";

export default {
    "ui:fieldset": [
        "sections"
    ],
    "sections": {
        "ui:layout": "section",
        "ui:title": "Innhold",
        "ui:preview": {
            "select": {
                "description": "description" 
            },
            prepare({formData = []}) {

                let description;

                if (formData.length && formData.length === 1) {
                    description = formData.length + " seksjon"
                } else if (formData.length) {
                    description = formData.length + " seksjoner"
                } else {
                    description = "Ingen seksjoner"
                }

                return {
                    "description": description
                }
            }
        },
        "ui:options" : {
            "collapsible": true,
            "expanded": true,
            "draggable": true,
            "spacing": 2,
        },
        "ui:buttons" : [
            {
                "title" : "Tekst",
                "item" : {
                    "sectionType" : "text"
                }
            },
            {
                "title" : "Media",
                "item" : {
                    "sectionType" : "media"
                }
            },
            {
                "title" : "DM",
                "item" : {
                    "sectionType" : "dm"
                }
            },
            {
                "title" : "Lenker",
                "item" : {
                    "sectionType" : "links"
                }
            },
            {
                "title" : "Vedlegg",
                "item" : {
                    "sectionType" : "attachments"
                }
            },
            {
                "title" : "Sitat",
                "item" : {
                    "sectionType" : "quote"
                }
            }
        ],
        "items": section
    }
}
