export default {
    "select": {
        "title": "title",
        "untitled": "untitled",
        "description": "description"
    },
    prepare({formData, formContext}) {
        const localeId = "locale:no"
        const title = formData.title && formData.title[localeId]

        const sectionType = formData.sectionType
        const untitled = "Untitled " + sectionType

        let description;

        if (sectionType === "text") {
            description = formData.bodytext && formData.bodytext[localeId]
        }

        if (sectionType === "media") {
            description = formData.media && formData.media.length + " media"
        }

        return {
            title: title,
            untitled: untitled,
            description: description
        }

    }
}
