import React, { Component } from 'react';
import { ImagePreview } from "@kit-ui/admin"
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
    root: {
        display: "block",
        position: "relative",
        backgroundColor: "#333",
        color: "white",

        "&:hover $preview": {
            opacity: .5,
        }

    },
    action: {
        zIndex: 2,
        position: "absolute",
        top: 0,
        right: 0,
        bottom: 0,
        left: 0,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",

        "& > [role=group]": {
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
        }

    },
    preview: {
        position: "absolute",
        top: 0,
        right: 0,
        bottom: 0,
        left: 0,
    }
}));

const LayoutButtons = () => { return <p>x</p>}

const KpLinKContentField = (props) => {

    const { formData, formContext, schema, uiSchema, registry} = props

    const {Â ObjectField } = registry.fields

    const newUiSchema = {
        "ui:fieldset": [
            "linkMedia",
            "linkContent",
            "linkReference",
        ],
        "ui:options": {
            "padding": 2,
            "spacing": 2
        },
        "linkMedia": {
            "ui:fieldset": [
                "mediaId",
                "media"
            ],
            "ui:sidebar": {
                "query": {
                    "models": "media",
                    "mediaType": "image"
                }
            },
            "ui:layout": "mediaPreview",
            "ui:buttons": [
                "sidebar"
            ],
        },
        "linkReference": {
            "ui:fieldset": [
                "referenceId",
                "reference"
            ],
            "ui:sidebar": {
                "query": {
                    "models": "documents",
                    "documentType": ["page*","article"]
                }
            },
            "ui:layout": "kioskLinkReference",
            "ui:options": {
                "editable": true,
                "deletable": true,
                "restorable": true
            }
        },
        "linkContent": {
            "ui:layout": "kioskLinkContent",
            "ui:grid": true,
            "ui:padding": 2,
            "ui:spacing": 2,
            "ui:fieldset": [
                "runningHead",
                "title",
                "description",
            ],
            "runningHead": {
                "ui:title": "Stikktittel"
            },
            "title": {
                "ui:title": "Tittel"
            },
            "description": {
                "ui:title": "Beskrivelse",
            },
        },
    }


    return <ObjectField {...props} uiSchema={newUiSchema} />

    return (
        <p>{JSON.stringify(uiSchema)}</p>
    )

}

export default KpLinKContentField
