import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import Button from '@material-ui/core/ButtonBase';
import IconButton from "@material-ui/core/IconButton"
import Icon from "@material-ui/core/Icon"

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import { makeStyles } from '@material-ui/core/styles';

const sizes = {
    "xlarge": "4:4",
    "xl": "4:4",
    "large": "4:3",
    "l": "4:3",
    "l-m": "4:2",
    "m-l": "2:4",
    "medium": "2:2",
    "m": "2:2",
    "m-s": "2:1",
    "s-m": "1:2",
    "small": "1:1",
    "s": "1:1",
    "xsmall": "0.5:0.5",
    "xs": "0.5:0.5",
}

const useStyles = makeStyles(theme => ({
    list: {
        position: "relative",
        padding: 0,
        margin: 0,
        width: props => { return props.cols * theme.spacing(7) },
        height: props => { return props.rows * theme.spacing(7) },
    },

    listItem: {
        position: "absolute",
        display: "block",
        top: 0,
        left: 0,
        backgroundColor: "rgba(0,0,0,.125)",
        zIndex: props => { return props.zIndex },

        outline: "1px solid white",
        width: props => { return props.cols * theme.spacing(7) },
        height: props => { return props.rows * theme.spacing(7) },

        "&:hover": {
            backgroundColor: "rgba(0,0,0,.5)",
            color: "white",
        },
        "&[aria-selected=true]": {
            backgroundColor: theme.palette.primary.main,
            color: "white"
        }
    },

    label: {
        position: "absolute",
        top: 'auto',
        right: 0,
        bottom: 0,
        left: 'auto',
        fontFamily: "Akkurat, sans-serif",
        textTransform: "uppercase",
        fontSize: '12px',
        padding: theme.spacing(0.5)
    }

}));

const SizeSettingsOptions = ({
    value, 
    options,
    onChange
}) => {

    if (!value) {
        value = options[0].value
    }

    const _onSelect = (value) => {
        onChange && onChange(value)
    }

    const maxRows = 4;
    const maxCols = 4;

    const ListOption = (option) => {
        const { label, value, selected } = option

        const grid = sizes && sizes[value]&& sizes[value].split(':') || [4,4]

        const cols = grid[0]
        const rows = grid[1]

        const zIndex = maxRows*maxCols - cols*rows

        const classes = useStyles({cols, rows, zIndex})

        return (
            <li className={classes.listItem} aria-selected={selected} onClick={() => _onSelect(value)}>
                <b className={classes.label}>{label || value}</b>
            </li>
        )

    }

    const classes = useStyles({cols: maxCols, rows: maxRows})

    return (
        <ul className={classes.list}>
            { options && options.map((option, index) => {
                return <ListOption {...option} key={index} selected={option.value === value}/>
            })}
        </ul>
    )

}

SizeSettingsOptions.defaultProps = {
}

SizeSettingsOptions.propTypes = {
}

export default SizeSettingsOptions;
