import React, { Component } from 'react';
import PropTypes from 'prop-types';

import ModuleFormat from "./ModuleFormat"
import ModuleBase from "./ModuleBase"
import ModuleHeader from "./ModuleHeader"
import ModuleBody from "./ModuleBody"
import ModuleFooter from "./ModuleFooter"

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    module: {
        position: "relative",
        zIndex: 2,

        "& + *": {
            zIndex: 1
        },

        display: "flex",
        flexDirection: "column",
        minHeight: props => { return props.minHeight }

    },
    header: {
        position: "absolute",
        zIndex: 2,
        top: 0,
        right: 0,
        bottom: "auto",
        left: 0,
        display: "flex",
        zIndex: 2,
        alignItems: "center",
        justifyContent: "flex-start",
        height: theme.spacing(6),
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        color: "inherit",

        "& + *": {
            zIndex: 1,
            marginTop: theme.spacing(6)
        }

    },
    body: {
        position: "relative",
        width: "100%",
        flexGrow: 1
    },
    footer: {
        borderTop: "1px solid",
        borderColor: theme.palette.divider,
        padding: props => { return theme.spacing(props.padding) }
    }

}));

const ExpandedModule = ({id, children, ...props }) => {
    const classes = useStyles(props)

    return (
        <ModuleBase className={classes.module} id={id} expanded={true} elevation={4}>
            <ModuleFormat {...props}>
                <ModuleHeader {...props} className={classes.header} />
                <ModuleBody className={classes.body}>
                    {children}
                </ModuleBody>
            </ModuleFormat>
            <ModuleFooter {...props} className={classes.footer} />
        </ModuleBase>
    )    

}

ExpandedModule.propTypes = {
    title: PropTypes.string,
    menu: PropTypes.array,
    buttons: PropTypes.array,
    children: PropTypes.node
}

export default ExpandedModule;
