import React, { Component } from 'react';

import ButtonCollapsible from "./ButtonCollapsible"
import NavToolbar from "../NavToolbar/NavToolbar"
import NavSettings from "../NavSettings/NavSettings"

import Typography from '@material-ui/core/Typography';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    header: {
        display: "flex",
        alignItems: "center",
        minHeight: theme.spacing(7),
        userSelect: "none",
        "&[aria-expanded=true]": {
            minHeight: theme.spacing(8)
        },
    },
    button: {
        display: "flex",
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),

        "& + $content": {
            marginLeft: 0
        }

    },
    content: {
        flexGrow: 1,
        maxWidth: "100%",

        marginLeft: theme.spacing(2),
        marginRight: theme.spacing(1),
        "&[data-editable=true]:hover": {
            cursor: "pointer",
        },
        "&[data-collapsible=true]:hover": {
            cursor: "pointer"
        }
    },
    title: {
        fontFamily: "Akkurat, sans-serif",
        fontSize: "18px",
        fontWeight: "normal",
        letterSpacing: 0,
    },
    description: {
        fontFamily: "Akkurat, sans-serif",
        fontSize: "16px",
        fontWeight: "normal",
        letterSpacing: 0,
        opacity: ".6",
        marginLeft: theme.spacing(1)
    },
    settings: {
        zIndex: 3,
        display: "flex",
        flexWrap: "none"
    },
    toolbar: {
        zIndex: 3,
        display: "flex",
        flexWrap: "none",

        "& button": {
            opacity: .5,

            "&:hover": {
                opacity: 1
            }

        }        
    },

}));

const SectionHeader = ({title, untitled = "Untitled", description, startAdornment, collapsible, editable, expanded, onToggle, ...props}) => {
    const classes = useStyles()

    if (!title && untitled) {
        title = untitled
    }

    const hasToggle = collapsible || editable
    const hideToolbar = editable && expanded

    return (
        <header className={classes.header} aria-expanded={expanded}>
            { startAdornment && <div className={classes.startAdornment}>{ startAdornment }</div> }
            { collapsible && <ButtonCollapsible expanded={expanded} className={classes.button} onClick={onToggle} /> }
            <Typography noWrap className={classes.content} data-collapsible={collapsible} onClick={hasToggle && onToggle}>
                <span className={classes.title} noWrap>{title}</span>
                { description && <span className={classes.description} variant="h3" noWrap>{description}</span> }
            </Typography>
            <NavSettings {...props} className={classes.settings} />
            {!hideToolbar && <NavToolbar {...props} className={classes.toolbar} /> }
        </header>
    )

}

export default SectionHeader;
