export var getOffset = function getOffset(el) {
  var rect = el.getBoundingClientRect();
  return {
    left: rect.left + window.scrollX,
    top: rect.top + window.scrollY
  };
};
export var getImagePointEvent = function getImagePointEvent(event, parent) {
  if (!parent) {
    parent = event.target.offsetParent;
  }

  var offset = getOffset(parent);
  var x = event.pageX - offset.left;
  var y = event.pageY - offset.top;
  var left = x / parent.offsetWidth * 100;
  var top = y / parent.offsetHeight * 100;
  var fx = (x / parent.offsetWidth - 0.5) * 2;
  var fy = (y / parent.offsetHeight - 0.5) * -2;
  return {
    x: fx,
    y: fy,
    top: top,
    left: left
  };
};
