export default {
    "select": {
        "imageUrl" : "imageUrl",
        "title" : "title",
        "description" : "description",
        "metadata" : "metadata",
        "typeLabel": "typeLabel"
    },
    prepare({formData, formContext}) {
        const { documentType, content = {} } = formData

        if (content) {

            const { titleImage, title } = content

            const imageUrl = titleImage && titleImage.media && titleImage.media.imageUrl

            let metadata = []

            return {
                imageUrl: imageUrl,
                title: title || "Uten tittel",
                description: false,
                metadata: metadata,
                typeLabel: documentType
            }

        }

        return {
            imageUrl: formData.imageUrl,
            title: formData.title || "Uten tittel",
            description: formData.description,
            metadata: [],
            typeLabel: documentType
        }
    }

}
