import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import ButtonBase from '@material-ui/core/ButtonBase';
import ListOptions from "./ListOptions"

import { makeStyles } from '@material-ui/core/styles';
import { utils } from '@kit-ui/schema';
const { getUiOptions } = utils

const useStyles = makeStyles(theme => ({
    button: {
        padding: theme.spacing(1.5),
        fontFamily: "Akkurat, sans-serif",
        fontSize: 12,
        fontWeight: "bold",
        lineHeight: 1,
    }
}));

const ListSettings = ({
    value, 
    options,
    uiSchema,
    onChange,
}) => {

    const {
        variant,
        enumOptions,
        enumDisabled
    } = options;

    const uiOptions = getUiOptions(uiSchema)

    if (!value) {
        value = enumOptions[0].value
    }

    let label

    enumOptions.map((option => {
        if (option.value === value) {
            label = option.label
        }
    }))

    const [expanded, setExpanded] = useState(false);
    const anchorRef = useRef(null)

    const _onChange = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const _onToggle = (event) => {
        setExpanded(prevExpanded => !prevExpanded);
    }

    const _onCollapse = (event) => {
        setExpanded(false);
    };

    const classes = useStyles()

    return (
        <React.Fragment>
            <ButtonBase className={classes.button} onClick={_onToggle} ref={anchorRef}>{label || value}</ButtonBase>
            <Dropdown expanded={expanded} anchorEl={anchorRef.current} onClickAway={_onCollapse}>
                <ListOptions options={uiOptions.enumOptions ||Â enumOptions} onChange={_onChange} />
            </Dropdown>
        </React.Fragment>
    )

}

ListSettings.defaultProps = {
}

ListSettings.propTypes = {
}

export default ListSettings;
