import React from 'react';
import PropTypes from "prop-types"
import Collapse from '@material-ui/core/Collapse';

import {
    EditableSection,
    CollapsibleSection,
    DefaultSection
} from "./"


const SectionLayout = ({collapsible, editable, ...props}) => {

    if (editable) {
        return <EditableSection {...props} />
    }

    if (collapsible) {
        return <CollapsibleSection {...props} />
    }

    return <DefaultSection {...props} />

    return (
        <SectionBase position={position} collapsible={collapsible ||Â editable} expanded={expanded}>
            <SectionHeader {...props} title={title} description={description} collapsible={collapsible} editable={editable} expanded={expanded} />
            <Collapse in={expanded} timeout="auto" unmountOnExit>
                <SectionBody position={position} {...props}>
                    { children }
                </SectionBody>
                { expanded && <SectionFooter><SectionButtons buttons={buttons} /></SectionFooter> }
            </Collapse>
        </SectionBase>
    )

}

SectionLayout.propTypes = {
    expanded: PropTypes.bool,
    onToggle: PropTypes.func
}

SectionLayout.defaultProps = {
    position: "relative",
    editable: false,
    collapsible: false,
    expanded: true
}

export default SectionLayout;
