import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import SectionTypeIcon from "./SectionTypeIcon"

import { makeStyles } from '@material-ui/core/styles';

import DmIcon from "./DmIcon"

const useStyles = makeStyles(theme => ({
    button: {
        padding: theme.spacing(1.5),
        fontFamily: "Akkurat, sans-serif",
        fontSize: 12,
        fontWeight: "bold",
        lineHeight: 1,
    }
}));


const SectionTypeOptions = ({
    value, 
    options,
    onChange
}) => {

    const _onSelect = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const classes = useStyles()

    const ListOption = (option) => {
        const { label, value } = option

        const icon = icons[value]

        return (
            <ListItem button onClick={() => _onSelect(value)}>
                { icon && <ListItemIcon><SectionTypeIcon icon={icon} /></ListItemIcon> }
                <ListItemText primary={label} />
            </ListItem>
        )

    }


    return (
        <List>
            { options && options.map((option, index) => {
                return <ListOption {...option} key={index} />
            })}
        </List>
    )

}

SectionTypeOptions.defaultProps = {
}

SectionTypeOptions.propTypes = {
}

export default SectionTypeOptions;
