import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import ButtonBase from '@material-ui/core/ButtonBase';
import Icon from "@material-ui/core/Icon"

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';


import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    button: {
        padding: theme.spacing(.5),
        margin: theme.spacing(.5),
//        border: "1px solid",
//        borderRadius: 2,

        fontFamily: "Akkurat, sans-serif",
        fontSize: 12,
        fontWeight: "bold",
        lineHeight: 1,
        maxHeight: 24,


    }
}));

const ListSettings = ({
    value, 
    options,
    onChange
}) => {

    const {
        variant,
        enumOptions,
        enumDisabled
    } = options;

    if (!value) {
        value = enumOptions[0].value
    }

    const [expanded, setExpanded] = useState(false);
    const anchorRef = useRef(null)

    const _onSelect = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const _onToggle = (event) => {
        setExpanded(prevExpanded => !prevExpanded);
    }

    const _onCollapse = (event) => {
        setExpanded(false);
    };


    const ListOption = (option) => {
        const {Â icon, label, value } = option

        return (
            <ListItem button onClick={() => _onSelect(value)}>
                { icon && <ListItemIcon><Icon>{icon}</Icon></ListItemIcon> }
                <ListItemText primary={label} />
            </ListItem>
        )

    }

    const classes = useStyles()

    return (
        <React.Fragment>
            <ButtonBase className={classes.button} onClick={_onToggle} ref={anchorRef}>{value}</ButtonBase>
            <Dropdown expanded={expanded} anchorEl={anchorRef.current} onClickAway={_onCollapse}>
                <List>
                    { enumOptions && enumOptions.map((option, index) => {
                        return <ListOption {...option} key={index} />
                    })}
                </List>
            </Dropdown>
        </React.Fragment>
    )

}

ListSettings.defaultProps = {
}

ListSettings.propTypes = {
}

export default ListSettings;
