export var getFiltersSchema = function getFiltersSchema(filters) {
  if (filters === void 0) {
    filters = ["brightness", "contrast", "blur"];
  }

  var properties = {};
  filters.map(function (filter) {
    properties[filter] = {
      type: "number"
    };
  });
  return {
    "type": "object",
    "properties": properties
  };
};
