import React from 'react';
import PropTypes from "prop-types"
import Collapse from '@material-ui/core/Collapse';

import {
    SectionBase,
    SectionHeader,
    SectionBody,
    SectionFooter,
    SectionButtons
} from "./"


const SectionLayout = ({position, collapsible, expanded, editable, title, description, buttons, children, ...props}) => {

    return (
        <SectionBase position={position} collapsible={collapsible ||Â editable} expanded={expanded}>
            <SectionHeader {...props} title={title} description={description} collapsible={collapsible} expanded={expanded} />
            <Collapse in={expanded} timeout="auto" unmountOnExit>
                <SectionBody position={position} {...props}>
                    { children }
                </SectionBody>
                { expanded && <SectionFooter><SectionButtons buttons={buttons} /></SectionFooter> }
            </Collapse>
        </SectionBase>
    )

}

SectionLayout.propTypes = {
    expanded: PropTypes.bool,
    onToggle: PropTypes.func
}

SectionLayout.defaultProps = {
    position: "relative",
    editable: false,
    collapsible: false,
    expanded: true
}

export default SectionLayout;
