import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import Button from '@material-ui/core/ButtonBase';
import IconButton from "@material-ui/core/IconButton"
import Icon from "@material-ui/core/Icon"

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import { makeStyles } from '@material-ui/core/styles';

import DmIcon from "./DmIcon"

const useStyles = makeStyles(theme => ({
    button: {
        padding: theme.spacing(1.5)
    }
}));

const icons = {
    "text": "subject",
    "media": "insert_photo",
    "links": "insert_link",
    "attachments": "attach_file",
    "quote": "format_quote",
    "dm": <DmIcon />
}

const SettingsIcon = ({icon}) => {

    if (!icon) {
        return false
    }

    if (icon && typeof icon === "string") {
        return (
            <Icon>{icon}</Icon>
        )
    }

    return icon

}

const ListSettings = ({
    value, 
    options,
    onChange
}) => {

    const {
        variant,
        enumOptions,
        enumDisabled
    } = options;

    if (!value) {
        value = enumOptions[0].value
    }

    const [expanded, setExpanded] = useState(false);
    const anchorRef = useRef(null)

    const _onSelect = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const _onToggle = (event) => {
        setExpanded(prevExpanded => !prevExpanded);
    }

    const _onCollapse = (event) => {
        setExpanded(false);
    };


    const ListOption = (option) => {
        const { label, value } = option

        const icon = icons[value]

        return (
            <ListItem button onClick={() => _onSelect(value)}>
                { icon && <ListItemIcon><SettingsIcon icon={icon} /></ListItemIcon> }
                <ListItemText primary={label} />
            </ListItem>
        )

    }

    const classes = useStyles()

    const SettingsButton = ({value, onClick}) => {

        const icon = icons[value]

        if (icon) {
            <IconButton className={classes.button} onClick={onClick}><SettingsIcon icon={icon} /></IconButton>
        }

        return (
            <Button className={classes.button} onClick={onClick}>{value}</Button>
        )

    }

    return (
        <React.Fragment>
            <SettingsButton className={classes.button} value={value} onClick={_onToggle} ref={anchorRef} />
            <Dropdown expanded={expanded} anchorEl={anchorRef.current} onClickAway={_onCollapse}>
                <List>
                    { enumOptions && enumOptions.map((option, index) => {
                        return <ListOption {...option} key={index} />
                    })}
                </List>
            </Dropdown>
        </React.Fragment>
    )

}

ListSettings.defaultProps = {
}

ListSettings.propTypes = {
}

export default ListSettings;
