import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import Button from '@material-ui/core/ButtonBase';
import IconButton from "@material-ui/core/IconButton"
import Icon from "@material-ui/core/Icon"

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import { makeStyles } from '@material-ui/core/styles';

import SectionTypeOptions from "./SectionTypeOptions"
import SectionTypeIcon from "./SectionTypeIcon"

import DmIcon from "./DmIcon"

const useStyles = makeStyles(theme => ({
    button: {
        padding: theme.spacing(1.5),
        fontFamily: "Akkurat, sans-serif",
        fontSize: 12,
        fontWeight: "bold",
        lineHeight: 1,
    }
}));


const SectionTypeSettings = ({
    value, 
    options,
    onChange
}) => {

    const {
        variant,
        enumOptions,
        enumDisabled
    } = options;

    if (!value) {
        value = enumOptions[0].value
    }

    let label

    enumOptions.map((option => {
        if (option.value === value) {
            label = option.label
        }
    }))

    const [expanded, setExpanded] = useState(false);
    const anchorRef = useRef(null)

    const _onChange = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const _onToggle = (event) => {
        setExpanded(prevExpanded => !prevExpanded);
    }

    const _onCollapse = (event) => {
        setExpanded(false);
    };

    const classes = useStyles()

    return (
        <React.Fragment>
            <Button className={classes.button} onClick={_onToggle} ref={anchorRef}>{label ||Â value}</Button>
            <Dropdown expanded={expanded} anchorEl={anchorRef.current} onClickAway={_onCollapse}>
                <SectionTypeOptions options={options} onChange={_onChange} />)
            </Dropdown>
        </React.Fragment>
    )

}

SectionTypeSettings.defaultProps = {
}

SectionTypeSettings.propTypes = {
}

export default SectionTypeSettings;
