export default {
    "select": {
        "description" : "description",
    },
    prepare({formData, formContext}) {
        const { images, videos } = formData

        let metadata = []

        if (images && images.length) {
            metadata.push(images.length + " bilder")
        }

        if (videos && videos.length) {
            metadata.push(videos.length + " videoer")
        }

        return {
            description: metadata.join(", "),
        }
    }
}
