import React, { Component } from 'react';
import { ListModule } from "@kit-ui/admin"
import { makeStyles } from '@material-ui/core/styles';

import IconButton from '@material-ui/core/IconButton';
import LinkIcon from '@material-ui/icons/Link';
import LinkOffIcon from '@material-ui/icons/LinkOff';


const useStyles = makeStyles(theme => ({
    reference: {
        border: "1px solid",
        borderColor: theme.palette.divider,

        "&:hover $preview": {
            opacity: .5,
        }

    },
}));

const KpLinkReferenceLayout = (props) => {

    const {Â referenceId, reference, onChange, onDialog } = props;

    const classes = useStyles()



    if (referenceId && reference) {
        return (
            <div className={classes.reference} onClick={() => onChange({referenceId: undefined, reference: {}})}>
                Remove ref
            </div>
        )
    }

    const LinkButton = ({onClick}) => {
        return (
            <IconButton onClick={onClick}>
                <LinkIcon />
            </IconButton>
        )
    }

    return (
        <div className={classes.reference}>
            <ListModule {...reference} startAdornment={<LinkButton onClick={onDialog} />} />
        </div>
    )

}

export default KpLinkReferenceLayout
