      width: "50%",
      marginLeft: "-50%",
      overflow: "hidden",
      transition: ".25s ease-out",
      "& + *": {
        transition: ".25s ease-out"
      },
      "&[aria-expanded=true]": {
        marginLeft: 0,
        "& + *": {
          marginLeft: "50%",
          marginRight: "-50%"
        }
      }
    }
  };
});

