import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"

import Button from '@material-ui/core/ButtonBase';
import IconButton from "@material-ui/core/IconButton"
import Icon from "@material-ui/core/Icon"

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import { makeStyles } from '@material-ui/core/styles';

import SizeSettingsOptions from "./SizeSettingsOptions"

const sizes = {
    "xlarge": "4:4",
    "xl": "4:4",
    "large": "4:3",
    "l": "4:3",
    "l-m": "4:2",
    "m-l": "2:4",
    "medium": "2:2",
    "m": "2:2",
    "m-s": "2:1",
    "s-m": "1:2",
    "small": "1:1",
    "s": "1:1",
    "xsmall": "0.5:0.5",
    "xs": "0.5:0.5",
}

const useStyles = makeStyles(theme => ({

    button: {
        padding: theme.spacing(1.5)
    },

    list: {
        position: "relative",
        padding: 0,
        margin: 0,
        width: props => { return props.cols * theme.spacing(7) },
        height: props => { return props.rows * theme.spacing(7) },
    },

    listItem: {
        position: "absolute",
        display: "block",
        top: 0,
        left: 0,
        backgroundColor: "rgba(0,0,0,.125)",
        zIndex: props => { return props.zIndex },

        outline: "1px solid white",
        width: props => { return props.cols * theme.spacing(7) },
        height: props => { return props.rows * theme.spacing(7) },

        "&:hover": {
            backgroundColor: "rgba(0,0,0,.5)",
            color: "white",
        },
        "&[aria-selected=true]": {
            backgroundColor: theme.palette.primary.main,
            color: "white"
        }
    },

    label: {
        position: "absolute",
        top: 'auto',
        right: 0,
        bottom: 0,
        left: 'auto',
        fontFamily: "Akkurat, sans-serif",
        textTransform: "uppercase",
        fontSize: '12px',
        padding: theme.spacing(0.5)
    }

}));

const SizeSettings = ({
    value, 
    options,
    onChange
}) => {

    const {
        variant,
        enumOptions,
        enumDisabled
    } = options;

    if (!value) {
        value = enumOptions[0].value
    }

    const [expanded, setExpanded] = useState(false);
    const anchorRef = useRef(null)

    const _onChange = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const _onToggle = (event) => {
        setExpanded(prevExpanded => !prevExpanded);
    }

    const _onCollapse = (event) => {
        setExpanded(false);
    };

    const maxRows = 4;
    const maxCols = 4;

    const classes = useStyles({cols: maxCols, rows: maxRows})

    return (
        <React.Fragment>
            <Button className={classes.button} onClick={_onToggle} ref={anchorRef}>{value}</Button>
            <Dropdown expanded={expanded} anchorEl={anchorRef.current} onClickAway={_onCollapse}>
                <SizeSettingsOptions options={enumOptions} onChange={_onChange} />
            </Dropdown>
        </React.Fragment>
    )

}

SizeSettings.defaultProps = {
}

SizeSettings.propTypes = {
}

export default SizeSettings;
