import React, { Component } from 'react';
import { ImagePreview } from "@kit-ui/admin"
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';


const useStyles = makeStyles(theme => ({
    root: {
        display: "block",
        position: "relative",
        backgroundColor: "#333",
        color: "white",

        "&:hover $preview": {
            opacity: .5,
        }

    },
    action: {
        zIndex: 2,
        position: "absolute",
        top: 0,
        right: 0,
        bottom: 0,
        left: 0,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",

        "& > [role=group]": {
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
        }

    },
    preview: {
        position: "absolute",
        top: 0,
        right: 0,
        bottom: 0,
        left: 0,
    }
}));

const LayoutButtons = () => { return <p>x</p>}

const KpLinkMediaField = (props) => {

    const {Â mediaId, media, referenceId, reference, onMediaDialog, onMediaChange } = props;

    const classes = useStyles()

    const onDialog = onMediaDialog
    const onChange = onMediaChange

    const mediaButtons = [
        {
            icon: "remove_circle",
            label: "Remove",
            variant: "text",
            size: "small",
            onClick: () => onChange({mediaId: undefined, media: {}})
        }
    ]

    const buttons = [
        {
            icon: "search",
            label: "Finn media",
            color: "inherit",
            size: "small",
            onClick: () => onDialog()
        }
    ]

    const buttonGroupProps = {
        "alignItems": "center",
        "flexDirection": "column"
    }

    const imageUrl = mediaId && media && media.imageUrl || referenceId && reference && reference.imageUrl;

    if (media && media.imageUrl) {
        return (
            <Grid item className={classes.root} data-name="media">
                <ImagePreview className={classes.preview} imageUrl={media.imageUrl} />
                <LayoutButtons className={classes.action} buttonGroupProps={buttonGroupProps} buttons={mediaButtons} />
            </Grid>
        )
    }

    if (referenceId && reference && reference.imageUrl) {
        return (
            <Grid item className={classes.root} data-name="media">
                <ImagePreview className={classes.preview} imageUrl={reference.imageUrl} />
                <LayoutButtons className={classes.action} buttonGroupProps={buttonGroupProps} buttons={buttons} />
            </Grid>
        )
    }

    return (
        <Grid item className={classes.root} data-name="media">
            <LayoutButtons className={classes.action} buttonGroupProps={buttonGroupProps} buttons={buttons} />
        </Grid>
    )

}

export default KpLinkMediaField
