import React, { forwardRef, useState, useEffect, useRef } from 'react';
import PropTypes from "prop-types"
import { Dropdown } from "@kit-ui/core"
import { makeStyles } from '@material-ui/core/styles';

import ButtonBase from '@material-ui/core/ButtonBase';
import SizeOptions from "./SizeOptions"

const useStyles = makeStyles(theme => ({

    button: {
        padding: theme.spacing(1.5),
        fontFamily: "Akkurat, sans-serif",
        fontSize: 12,
        fontWeight: "bold",
        lineHeight: 1,
    },

}));

const SizeSettings = ({
    value, 
    options,
    onChange
}) => {

    const {
        variant,
        enumOptions,
        enumDisabled
    } = options;

    if (!value) {
        value = enumOptions[0].value
    }

    const [expanded, setExpanded] = useState(false);
    const anchorRef = useRef(null)

    const _onChange = (value) => {
        onChange && onChange(value)
        setExpanded(false)
    }

    const _onToggle = (event) => {
        setExpanded(prevExpanded => !prevExpanded);
    }

    const _onCollapse = (event) => {
        setExpanded(false);
    };

    const maxRows = 4;
    const maxCols = 4;

    const classes = useStyles({cols: maxCols, rows: maxRows})

    return (
        <React.Fragment>
            <ButtonBase className={classes.button} onClick={_onToggle} ref={anchorRef}>{value}</ButtonBase>
            <Dropdown expanded={expanded} anchorEl={anchorRef.current} onClickAway={_onCollapse}>
                <SizeOptions options={enumOptions} onChange={_onChange} />
            </Dropdown>
        </React.Fragment>
    )

}

SizeSettings.defaultProps = {
}

SizeSettings.propTypes = {
}

export default SizeSettings;
