var getImageOffset = function getImageOffset(element) {
  var rect = element.getBoundingClientRect();
  var win = element.ownerDocument.defaultView;
  return {
    top: rect.top + win.pageYOffset,
    left: rect.left + win.pageXOffset
  };
};

export var getImagePointEvent = function getImagePointEvent(event) {
  var imgW = event.target.width;
  var imgH = event.target.height;
  var imgOffset = getImageOffset(event.target);
  var offsetX = event.pageX - imgOffset.left;
  var offsetY = event.pageY - imgOffset.top;
  var x = (offsetX / imgW - 0.5) * 2;
  var y = (offsetY / imgH - 0.5) * -2;
  var left = offsetX / imgW * 100;
  var top = offsetY / imgH * 100; // reticle

  return {
    left: left,
    top: top,
    x: x,
    y: y
  };
};
export default getImagePointEvent;